import React, { Component } from 'react'

import axios from 'axios';
import { Link } from 'react-router-dom';

//global variable for restapi url
const url=`https://jsonplaceholder.typicode.com/users` //array type of object
 class restapi extends Component {

  constructor(props){
      super(props)
      this.state=
      {
         userdata:[]
      }
  }

  //delete the data
  DeleteHandler=(id)=>
  {
    
     if( window.confirm(`delete the record number is ${id}?`)){

        axios.delete(`${url}/${id}`)
        axios.g(`${url}/${id}`)
     }
  }

  render() {
    return (<>
      <div className="container p-5">
        <h3 class="text-center">FETCH RESTAPI DATA USING GLOBAL URL/CLIENTS</h3>
     
        <table className="table table-dark table-bordered">
            <thead className="table table-light">
                <tr>
                    <th>ID</th>
                    <th>NAME</th>
                    <th>EMAIL</th>
                    <th>website</th>
                    <th colSpan="3" className="text-center">ACTION</th>
                </tr>
            </thead>
            
            
            <tbody>
            {
         this.state.userdata.map((res,i)=>
         {
            return(<tr key={i}>
             <td>{res.id}</td>
             <td>{res.name}</td>
             <td>{res.email}</td>
             <td>{res.website}</td>
             <td>
                 <Link to={`/restapi/view/${res.id}`}  class="btn btn-primary">VIEW</Link>
             </td>
             <td>
                 <Link to={`/restapi/edit/${res.id}`} class="btn btn-warning">Edit</Link>
             </td>
             <td>
                 <button onClick={()=> this.DeleteHandler(res.id)} class="btn btn-danger">Delete</button>
             </td>
            </tr>)
         })
     }
            </tbody>



        </table>

     
     
     
     
      </div>
      </>)
  }

  //restapi calls
  componentDidMount()
  {
      axios.get(url).then((result)=> this.setState({userdata:result.data}));
  }
}
export default restapi
