import {React} from 'react'

//pager
export default (props)=>
{
    //undefined array
    var numbers=[]

    var {totalPostsInAPI,MaxDisplayrecordsPerButton}=props

     var numberOfButtons=Math.ceil(totalPostsInAPI/MaxDisplayrecordsPerButton);

    for(let i=1;i<=numberOfButtons;i++)
    {
       numbers.push(i) 
    }
    return(<>
         <div class="conatiner p-5">

            <ul class="pagination">
            {
                numbers.map((bno,i)=> <li class="page-item">
                <a class="page-link" onClick={()=>getButtonNumber}>{bno}</a></li>)
            }
            </ul>
         </div>
    </>)
}
