import React, { component,useState,useEffect } from 'react'
import axios from 'axios'
import { Link,useParams } from 'react-router-dom'
var idno 
const url = " http://localhost:3001/users"
export default (props)=> {
    let {idno} = useParams()
    let[usedata,setStateusedata] =useState({
        name:'',
        email:'',
        phone:'',
        txtconformation:''
    })

    var { name, email, phone, txtconformation } = usedata

   
    // nameHandling=(event)=>
    // {
    //     this.setState({name: event.target.value})
    // }
    // emailHandling=(event)=>
    // {
    //     this.setState({emial: event.target.value})
    // }
    // phoneHandling=(event)=>
    // {
    //     this.setState({phone: event.target.value})
    // }

    useEffect(()=> {
      
        axios.get(`${url}/${idno}`).then((result) => setStateusedata(result.data))
    },[])

    let formDataHandling = (e) => {
        setStateusedata({ ...usedata,[e.target.name]: e.target.value });
    }


    let submitHandler=(e)=>
    {
        e.preventDefault()
     var FormData=

    {
       name :`${usedata.name}`,
       emial :`${usedata.email}`,
       phone :`${usedata.phone}`
    }

    axios.put(`${url}/${idno}`,FormData)
.then(()=>props.history.push('/Hooksuser'))
}
        
        return (
            <div className='container p-5'>
                <h2>Edit Component Page</h2>

                <form onSubmit={submitHandler.bind(this)}>
                    {/* <div className="mb-3">
                        <label className="form-label" >ID</label>
                        <input type="number" className="form-control" value={id} />
                    </div> */}
                    <div className="mb-3">
                        <label className="form-label" >Name</label>
                        <input type="text" className="form-control"  value={name} name='name' onChange={formDataHandling.bind(this)} />
                    </div>
                    <div className="mb-3">
                        <label className="form-label" >Email</label>
                        <input type="email" className="form-control"value={email} name='emAIL' onChange={formDataHandling.bind(this)}/>
                    </div>
                    <div className="mb-3">
                        <label className="form-label" >Phone</label>
                        <input type="number" className="form-control" value={phone} name='phone' onChange={formDataHandling.bind(this)} />
                    </div>
               


                <Link className='btn btn-success mx-3' to='/Hooksuser'>Go Back</Link>
                <button type='submit' className='btn btn-dark text-white'value='update'>Update</button>
                </form>
            </div>
        )
    }
