    useEffect(()=>{
        axios.get(apiurl).then
    })

    return(<>
           <div class="container p-3">
                <h1>React UseEffect Hooks for API calls</h1>
                   
                    {stateEmail} <br></br>

                    <button onClick={handleStateEmail}>Update</button>
