import React, { Component } from 'react'
import axios from 'axios'
import { useState} from 'react'



const url = "http://localhost:3006/users"

export default (props) => {
let [usedata, setStateusedata] = useState({
        name: '',
        email: '',
        phone: '',
        txtconformation: ''

    })

    var { name, email, phone, txtconformation } = usedata



    // nameHandling=(event)=>
    // {
    //     this.setState({name: event.target.value})
    // }
    // emailHandling=(event)=>
    // {
    //     this.setState({emial: event.target.value})
    // }
    // phoneHandling=(event)=>
    // {
    //     this.setState({phone: event.target.value})
    // }


    let formDataHandling = (e) => {
        setStateusedata({ ...usedata,[e.target.name]: e.target.value });
    }
    let submitHandler = (e) => {
        e.preventDefault()
        var FormData =

        {
            name: `${usedata.name}`,
            emial: `${usedata.email}`,
            phone: `${usedata.phone}`
        }
        alert(JSON.stringify(FormData));

        // axios.post(url,FormData)
        // .then(()=>window.alert('account created'))
        // .catch(()=>window.alert('account failed'))


        //   axios.post(url,FormData)
        //     .then(()=>this.setState({txtconformation:'account created'}))
        //     .catch(()=>this.setState({txtconformation:'account failed'}))

        axios.post(url, FormData)
            .then(() => {
            props.history.push('/Hooksuser')
            
            })

    }



    return (
        <div className='container p-4'>
            <h4 className='text-center py-3'>User Registration</h4>
            <div className='container w-50'>
                <form onSubmit={submitHandler.bind(this)}>
                    <div className="mb-3">
                        <label className="form-label">Name</label>
                        <input type="text" className="form-control" value={name} name='name' onChange={formDataHandling.bind(this)} />
                    </div>

                    <div className="mb-3">
                        <label className="form-label">Email</label>
                        <input type="email" className="form-control" value={email} name='emial' onChange={formDataHandling.bind(this)} />
                    </div>

                    <div className="mb-3">
                        <label className="form-label">Phone</label>
                        <input type="number" className="form-control" value={phone} name='phone' onChange={formDataHandling.bind(this)} />
                    </div>

                    <div className="mb-3">

                        <button type="submit" className="btn btn-primary">Create Account </button>
                        <p className='text-primary'>{txtconformation}</p>
                    </div>
                </form>

            </div>
        </div>
    )
}
