import React, { Component } from 'react'
import axios from 'axios';
import { Link } from 'react-router-dom';
//global
var idno;
//global variable for restapi url

 class view extends Component {

constructor(props) {
  super(props)

  this.state = {
     
  }
}

  render() {
       idno=this.props.match.params.idno
      //this.props history,location,match
      console.log(idno)

      var {id,name,email,phone}=this.state

    return (<>
      <div className="container p-5">
         <h2>VIEW COMPONENT PAGE</h2>
      <p>
          {idno} th record data
      </p>

      <ul>
          <li>{id}</li>
          <li>{name}</li>
          <li>{email}</li>
          <li>{this.state.phone}</li>
      </ul>

          <Link className="btn btn-success" to="/restapi">GO BACK</Link>
      
      </div>
      </>)
  }

  //restapi calls
  componentDidMount()
  {
    const url=`https://jsonplaceholder.typicode.com/users/${idno}` 
      axios.get(url).then((result)=> this.setState(result.data));
  }
}
export default view
