import{React, useState,useEffect} from 'react'
 
import axios from 'axios'

const apiurl=`https://fakestoreapi.com/products`

export default (props)=>{

     //STATE OBJECT IMPLEMENTATION
    var [products,setStateProducts] = useState([]);

    useEffect(()=>
    {
        axios.get(apiurl).then((result)=>setStateProducts(result.data))
    },[])

    return(<>
           <div class="container p-3">
                <h1>React UseEffect Hooks for API calls</h1>

                 <div className="row">
                    {
                        products.map((res,i)=>
                        {
                            return(<div className="clo-lg-3" key={i}>

<div className="card">
<img src={res.image} class="card-img-top w-50 mx-auto" style={{height:'200px'}} />
<div className="card-body text-center">
<h5 className="card-title">Card title</h5>
<p className="card-text">{res.description.slice(0,60)}</p>
<a href="#" className="btn btn-primary">Go somewhere</a>
</div>
</div>  

                            </div>)
                        })
                    }

                 </div>
           </div>
    </>)
}
