import React, { useEffect, useState } from 'react'
import axios, { Axios } from 'axios';

import { Link, useParams } from 'react-router-dom';

//global
var idno;

const url='http://localhost:3001/users'

export default (props)=> {
  
    let [userData,setStateUserData]=useState=({
        name:'',
        email:'',
        phone:'',
        txtConformation:''
    });
    
  //get parameter value
  let {idno} = useParams();

  let formDatahandling = (e)=>{
    setStateUserData({...userData,[e.target.name]:e.target.value});
}

useEffect(()=>{
    axios.get(`${url}/${idno}`).then((result)=> setStateUserData(result.data))

},[]);
let submitHandler =(e) =>
{
  e.preventDefault();

  var formData=
  {
    name:`${userData.name}`,
    email:userData.email,
    phone:userData.phone
  }
     axios.put(`${url}/${idno}`,formData)
     .then(()=>{
      // window.alert("...UPDATED...");
       props.history.push('/hooksuser')
     });
}


    
   

     //destructuring
     var { name, email, phone, txtConformation } = userData
    return (<>
      <div className="container p-5">
         <h3 className="text-center">EDIT COMPONENT PAGE</h3>
        
        <form onSubmit={submitHandler.bind(this)}>
             
             <div class="mb-3">
               <label class="form-label">Name</label>
               <input type="text" class="form-control"
                value={name} name={name}
                onChange={formDatahandling.bind(this)}/>
             </div>

             <div class="mb-3">
               <label class="form-label">Email</label>
               <input type="email" class="form-control" 
               value={email} name={email} 
               onChange={formDatahandling.bind(this)}/>
             </div>

             <div class="mb-3">
               <label class="form-label">Phone</label>
               <input type="number" class="form-control" 
               value={phone} name={phone}
               onChange={formDatahandling.bind(this)} />
             </div>

             <div class="mb-3">
             <Link className="btn btn-success mx-2" to="/hooksuser">GOTO BACK</Link>
               <button type="number" class="btn btn-warning text-white" value="Update">Update</button>
             </div>

        </form>
        
        </div>
    </>)
  }
