import{React, useState,useEffect} from 'react'
 
import axios from 'axios'
 import Pager from './pager.js'

const apiurl=`https://jsonplaceholder.typicode.com/posts`

export default (props)=>{

     //STATE OBJECT IMPLEMENTATION
    var [posts,setStatePosts] = useState([]);

    //SELECT DEFAULT MAX POSTS PER ACTION
     var [DefaultMaxDispplayposts] = useState(10);

    useEffect(()=>
    {
        axios.get(apiurl).then((result)=>setStatePosts(result.data))
    },[])

    //totalpost from api
    var totalPosts= posts.length

    return(<>
           <div class="container p-3">
                <h1 class="text-center h4">React Hooks pagination</h1>
                
                <br></br>
                <Pager totalPostsInAPI={totalPosts}
                 DefaultMaxDisplayrecordsPerButton={DefaultMaxDispplayposts}
                />
                 <br></br>
                    {
                       posts.map((res,i)=>
                        {
                            return(<ul key={i}>

                                <li>
                                    <h4>{res.id},{res.title}</h4>
                                    <p>{res.body}</p>
                                </li>

                            </ul>)
                        })
                    }

                 </div>
           
    </>)
}
