import React, { Component } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'
//global
var idno;

export default class 
 extends Component {
     constructor(props) {
       super(props)
     
       this.state = {}
     }
  render() {
      idno=this.props.match.params.idno
      //this.props -> history location match
      console.log(idno)

      var {id, name, email, phone} = this.state

    return (<>
      <div className="container p-5">
         <h2>EDIT COMPONENT PAGE</h2>
      
      <form>

           <div class="mb-3">
               <label class="form-label">Id</label>
                <input type="email" class="form-control" value="100" />
           </div>

           <div class="mb-3">
               <label class="form-label">Name</label>
                <input type="text" class="form-control" value="name" />
           </div>

           <div class="mb-3">
               <label class="form-label">Email</label>
                <input type="email" class="form-control" value="" />
           </div>
      </form>
      
      <Link className="btn btn-success" to="/restapi">GOTO BACK</Link>
      </div>
      </> )
  }
   //restapi calls
   componentDidMount()
   {
     const url=`https://jsonplaceholder.typicode.com/users/${idno}` 
       axios.get(url).then((result)=> this.setState(result.data));
   }
}
