    {
        this.props.empinfo.map((result,i)=>
        {
           return(<ul key={i}>
             <li>{result.id}</li>
             <li>{result.name}</li>
             <li>{result.email}</li>

           </ul>)
        })
    }
 


</>)
}
}

//step-6
const mapStateToProps=(state)=>{
    //get the data from the redux store by using reducers
   console.log(state)
    return({
    empdata:state.empReducer
}) 
}

//step-5
const mapDispatchToProps=(Dispatch)=>{
    //calling redux actions
    return bindActionCreators({emp},Dispatch) 
}

//export default ReactReduxConnection
//step-2
export default connect(mapStateToProps,mapDispatchToProps)(Contact);
