import React, { Component } from 'react'
import axios from 'axios';

import { Link } from 'react-router-dom';

//global
var idno;
export default class edit extends Component {
  
  constructor(props) {
    super(props)
  
    this.state =
    {
      uname:'',
      email:'',
      phone:'',
      txtConformation:''
    }
  }
  render() {
    idno = this.props.match.params.idno
    console.log(idno)

     //destructuring
     var { uname, email, phone, txtConformation } = this.state
    return (<>
      <div className="container p-5">
         <h3 className="text-center">EDIT COMPONENT PAGE</h3>
        
        <form onSubmit={this.submitHandler.bind(this)}>
             
             <div class="mb-3">
               <label class="form-label">Name</label>
               <input type="text" class="form-control"
                value={uname} 
                onChange={this.unameHandling.bind(this)}/>
             </div>

             <div class="mb-3">
               <label class="form-label">Email</label>
               <input type="email" class="form-control" 
               value={email} 
               onChange={this.emailHandling.bind(this)}/>
             </div>

             <div class="mb-3">
               <label class="form-label">Phone</label>
               <input type="number" class="form-control" 
               value={phone}
                />
             </div>

             <div class="mb-3">
             <Link className="btn btn-success mx-2" to="/restapi">GOTO BACK</Link>
               <button type="number" class="btn btn-warning text-white" value="Update">Update</button>
             </div>

        </form>
        
        </div>
    </>)
  }
  //restapi calls
  componentDidMount()
  {
    const url=`https://jsonplaceholder.typicode.com/users/${idno}` 
      axios.get(url).then((result)=> this.setState(result.data));
  }
}
