import React, { Component } from 'react'

import axios from 'axios';

//global variable for restapi url
const url=`https://jsonplaceholder.typicode.com/users` //array type of object
 class restapi extends Component {

  constructor(props){
      super(props)
      this.state=
      {
         userdata:[]
      }
  }

  render() {
    return (<>
      <div className="container p-5">
        <h3 class="text-center">FETCH RESTAPI DATA USING GLOBAL URL/CLIENTS</h3>
     
     
     
     
      </div>
      </>)
  }

  //restapi calls
  componentDidMount()
  {
      axios.get(url).then((result)=> this.setState({userdata:result.data}));
  }
}
export default restapi
